mapper.update:
UPDATE member 
   SET pwd=#{pwd}, name=#{name}, email=#{email}, postNum=#{postNum}, addr1=#{addr1}, addr2=#{addr2}, phone=#{phone}
 WHERE id=#{id}

mapper.select:
SELECT *
  FROM member
 WHERE name=#{name}
   AND email=#{email}

SELECT *
  FROM member
 WHERE id=#{id}
   AND name=#{name}
   AND email=#{email}
